import { createStore } from 'vuex'

//https://www.youtube.com/watch?v=htYYSszfzv0&t=5501s
// 1:20:39

const store = createStore({
    state(){
        return {
            account:{
                email:0
            }
        }
    },
    metations: {
        setAccount(state, payload){
            state.account.id = payload;
        }
    }
})

export default store;
