import { createRouter, createWebHistory } from 'vue-router';

const routes = [
  {
    path: '/',
    name: 'HomeView',
    component: () =>
      import(/* webpackChunkName: "login" */ '../views/HomeView.vue'),
  },
  {
    path: '/',
    name: 'HomeLoginView',
    component: () =>
      import(/* webpackChunkName: "login" */ '../views/HomeLoginView.vue'),
  },
  {
    path: '/login',
    name: 'LoginView',
    component: () =>
      import(/* webpackChunkName: "login" */ '../views/LoginView.vue'),
  },
  {
    path: '/signup',
    name: 'SignupView',

    component: () =>
      import(/* webpackChunkName: "login" */ '../views/SignupView.vue'),
  },
];

const router = createRouter({
  history: createWebHistory(process.env.BASE_URL),
  routes,
});

// 전역 가드
router.beforeEach((to, from, next) => {
  // 1) 루트(‘/’)로 진입하는데 이미 로그인된 상태라면
  if (to.path === '/' && store.state.account.username !='') {
    // 2) ‘DashboardView’(예시)로 리다이렉트
    return next({ name: 'JomeLoginView' })
  }
  // 그 외에는 그냥 진행
  next()
})

export default router;
