import { createStore } from 'vuex'

//https://www.youtube.com/watch?v=htYYSszfzv0&t=5501s
// 1:20:39


createStore({
    state(){
        return {
            user: {}
        }
    },
    metations: {
        setUser(state,value){
            state.user = value;
        }
    }
})
